data.default.conf:
charset     utf-8;
gzip on;
gzip_disable "msie6";

# TODO: explore if we can use paths instead of subdomains...?

# Default configuration
server {
  listen 80 default_server;
  listen [::]:80 default_server;

  root /var/www/html;

  # Add index.php to the list if you are using PHP
  index index.html index.htm index.nginx-debian.html;

  server_name $hostname;

  location / {
    # return empty response
    return 204;
  }

  # Proxy ELB status
  location ~ /elb/([^/]*)/(.*)$ {
    resolver 127.0.0.1;
    proxy_pass http://127.0.0.1/$2;
    proxy_set_header Host $1;
    proxy_intercept_errors on;
    access_log  off;
  }
}

# KoBoCAT HTTP.
server {
  listen      80;
  server_name kc.kobo.local kc.docker.internal kc.example.com;

  # Allow 100M upload
  client_max_body_size 100M;
  # Support bigger headers. Useful for huge cookies
  large_client_header_buffers 8 16k;

  location / {
    uwsgi_read_timeout 130;
    uwsgi_send_timeout 130;
    uwsgi_pass localhost:8001;
    # For setting HTTP headers, see http://stackoverflow.com/a/14133533/1877326.
    uwsgi_param HTTP_X_REAL_IP $remote_addr;
    uwsgi_param HTTP_X_FORWARDED_FOR $remote_addr;
    # uwsgi_param HTTP_X_FORWARDED_PROTO $scheme;
    include /etc/nginx/uwsgi_params;

    # Support longer query strings. See issue #147
    uwsgi_buffers 8 16k;
    uwsgi_buffer_size 16k;
  }

  location /static {
    alias /srv/www/kobocat;
  }

  # media files
  location /protected/ {
    internal;
    alias /media/;
  }
}

# KoBoForm HTTP.
server {
  listen      80;
  server_name kobo.kobo.local kobo.docker.internal kobo.example.com;

  # Allow 100M upload
  client_max_body_size 100M;
  # Support bigger headers. Useful for huge cookies
  large_client_header_buffers 8 16k;

  location ~ ^/forms/(.*) {
    return 301 /$1;
  }

  location /static {
    alias /srv/www/kpi;

    # gzip configs from here
    # http://stackoverflow.com/a/12644530/3088435
    gzip on;
    gzip_disable "msie6";
    gzip_comp_level 6;
    gzip_min_length 1100;
    gzip_buffers 16 8k;
    gzip_proxied any;
    gzip_types
      text/plain
      text/css
      text/js
      text/xml
      text/javascript
      application/javascript
      application/x-javascript
      application/json
      application/xml
      application/xml+rss;
  }

  # public media, e.g. custom logos (KPI `ConfigurationFile`s)
  location /media/__public {
      alias /srv/kpi_media/__public;
  }

  error_page 418 = /static/html/Offline.html;

  location / {
    uwsgi_read_timeout 130;
    uwsgi_send_timeout 130;
    uwsgi_pass localhost:8003;
    # For setting HTTP headers, see http://stackoverflow.com/a/14133533/1877326.
    uwsgi_param HTTP_X_REAL_IP $remote_addr;
    uwsgi_param HTTP_X_FORWARDED_FOR $remote_addr;
    # uwsgi_param HTTP_X_FORWARDED_PROTO $scheme;
    include /etc/nginx/uwsgi_params;

    # Support longer query strings. See issue #147
    uwsgi_buffers 8 16k;
    uwsgi_buffer_size 16k;
  }
}
